import React from 'react';
import { Flex, Box, Heading } from '@chakra-ui/core';
import { FaHeading } from 'react-icons/fa';

const Table = (props) => {
    const { columns, data, title, ...rest } = props;

    return (
        <Box
            {...rest}
            backgroundColor="white"
            rounded="md"
            p={5}    
        >
            <Flex>
                {
                    columns.map((column, ix) => (
                        <Box
                            mr="10px"
                        >
                            <Heading>
                                { column.label }
                            </Heading>
                        </Box>
                    ))
                }
            </Flex>
        </Box>
    )
}

export default Table;
