import React, { useState, useEffect } from 'react';
import { Flex, Box, Heading, ColorModeProvider } from '@chakra-ui/core';

const Table = (props) => {
    const { columns, data, title, ...rest } = props;
    //const fields = [];

    const [bindedCols, setBindedCols] = useState([]);
    const [bindedData, setBindedData] = useState([]);

    const bindColumnsAndData = () => {
        const binded = [];
        const fields = [];

        columns.map((column, ix) => {
            fields.push({name: column.field, index: ix});
        })

        fields.map(field => {
            data.map(dt => {
                if (dt[field.name]) {
                    binded[field.index] = dt;
                }
            })
        })

        console.log(binded);
    }

    useEffect(() => {
        bindColumnsAndData();
    }, [])

    return (
        <Box
            {...rest}
            backgroundColor="white"
            rounded="md"
            p={5}    
        >
           <Flex
            mb={5}
           >
                <Heading
                    as="h5"
                    size="sm"
                >
                    { title }
                </Heading>
           </Flex>
            <Flex
                borderBottom="1px"
                borderTop="1px"
                p="5px"
            >
                {
                    columns.map((column, ix) => (
                        <Box
                            key={ix}
                            mr="10px"
                            w={column.size ? column.size : `${Math.round(100/columns.length)}%`}
                            cursor="pointer"
                        >
                            <Heading
                                as="h5"
                                size="sm"
                            >
                                { column.label }
                            </Heading>
                        </Box>
                    ))
                }
            </Flex>

        </Box>
    )
}

export default Table;
