import React from 'react';
import { Flex, Box, Heading, Divider } from '@chakra-ui/core';
import { FaHeading } from 'react-icons/fa';

const Table = (props) => {
    const { columns, data, title, ...rest } = props;

    return (
        <Box
            {...rest}
            backgroundColor="white"
            rounded="md"
            p={5}    
        >
           <Flex
            mb={5}
           >
                <Heading
                    as="h5"
                    size="sm"
                >
                    { title }
                </Heading>
           </Flex>
            <Divider />
            <Flex>
                {
                    columns.map((column, ix) => (
                        <Box
                            mr="10px"
                            w={column.size ? column.size : `${Math.round(100/columns.length)}%`}
                        >
                            <Heading
                                as="h5"
                                size="sm"
                            >
                                { column.label }
                            </Heading>
                        </Box>
                    ))
                }
            </Flex>
            <Divider />
        </Box>
    )
}

export default Table;
