import React, { useState, useEffect } from 'react';
import { Flex, Box, Heading, InputLeftElement, InputGroup, Icon, Input } from '@chakra-ui/core';
import * as parser from '../../helpers/parser';

const Table = (props) => {
    const { columns, data, title, ...rest } = props;

    const [filtered, setFiltered] = useState([]);

    useEffect(() => {
        setFiltered(data);
    }, [])

    return (
        <Box
            {...rest}
            backgroundColor="white"
            rounded="md"
            p={5}    
        >
            <Heading
                as="h5"
                size="sm"
                mb="10px"
            >
                { title }
            </Heading>
           <Flex
            mb={5}
           >
