                </InputGroup>
           </Flex>
            <Flex
                borderBottom="1px"
                borderTop="1px"
                p="5px"
            >
                {
                    columns.map((column, ix) => (
                        <Box
                            key={ix}
                            mr="10px"
                            w={column.size ? column.size : `${Math.round(100/columns.length)}%`}
                            cursor="pointer"
                        >
                            <Heading
                                as="h5"
                                size="sm"
                            >
                                { column.label }
                            </Heading>
                        </Box>
                    ))
                }
            </Flex>
            <Box
                mt="20px"
            >
                {
                    filtered.map((dt,ix) => (
                        <Flex
                            key={ix}
                            backgroundColor={
                                ix % 2 === 0
                                ? 'blue.50'
                                : null
                            }
                        >
                            {
                               columns.map((column, ix) => (
                                    <Box
                                        key={ix}
                                        w={column.size ? column.size : `${Math.round(100/columns.length)}%`}
                                        py="5px"
                                        px="5px"
                                    >
                                       { parser.parse({data: dt[column.field], format: 'display'}, column.type) }
                                    </Box>
                               ))
                            }
                        </Flex>
                    ))
                }
            </Box>
        </Box>
    )
}

export default Table;
