import React from 'react';
import { Flex, Box, Heading, Divider } from '@chakra-ui/core';

const Table = (props) => {
    const { columns, data, title, ...rest } = props;

    return (
        <Box
            {...rest}
            backgroundColor="white"
            rounded="md"
            p={5}    
        >
           <Flex
            mb={5}
           >
                <Heading
                    as="h5"
                    size="sm"
                >
                    { title }
                </Heading>
           </Flex>
            <Flex
                borderBottom="1px"
                borderTop="1px"
                p="5px"
                backgroundColor="blue.800"
            >
                {
                    columns.map((column, ix) => (
                        <Box
                            key={ix}
                            mr="10px"
                            w={column.size ? column.size : `${Math.round(100/columns.length)}%`}
                            cursor="pointer"
                        >
                            <Heading
                                as="h5"
                                size="sm"
                            >
                                { column.label }
                            </Heading>
                        </Box>
                    ))
                }
            </Flex>
        </Box>
    )
}

export default Table;
